export const getFormatDate = (str) => {
  let dt = new Date(str);

  return `${(dt.getDate()+1).toString().padStart(2, '0')}/${
    dt.getMonth().toString().padStart(2, '0')}/${
    dt.getFullYear().toString().padStart(4, '0')}`;
};

export const getFormatTime = (str) => {
  let dt = new Date(str);

  return `${dt.getHours().toString().padStart(2, '0')}:${
    dt.getMinutes().toString().padStart(2, '0')}:${
    dt.getSeconds().toString().padStart(2, '0')}`;
}


