export const getDifference = (a, b) => {
  const result = ((Math.abs(new Date(a) - new Date(b))) / 60000).toFixed(2);
  if (result > 60000) {
    return 'Test faild';
  }

  return result;
};

export const getProgessBarValue = (data) => {
  // 10 count of questions in test
  if (data > 0) return `${(data * 100 / 10)}%`;
  if (!data) return '0%';
};